pub fn main() {
    use std::env;
    use std::process;
    let args: Vec<String> = env::args().collect();
    let argc = args.len();
    if argc < 6 {
        eprintln!("Error: Insufficient arguments");
        printUsage();
        process::exit(1);
    }
    let student_name = &args[1];
    let age: i32 = match args[2].parse() {
        Ok(a) if (1..=120).contains(&a) => a,
        _ => {
            eprintln!("Error: Invalid age (must be between 1 and 120)");
            process::exit(1);
        }
    };
    let course_name_in = &args[3];
    let course_code: i32 = match args[4].parse() {
        Ok(code) if code > 0 => code,
        _ => {
            eprintln!("Error: Invalid course code");
            process::exit(1);
        }
    };
    let num_grades = argc - 5;
    let mut grades: Vec<f32> = Vec::with_capacity(num_grades);
    for i in 0..num_grades {
        let s = &args[5 + i];
        let g: f32 = match s.parse::<f32>() {
            Ok(v) if (0.0..=100.0).contains(&v) => v,
            Ok(v) => {
                eprintln!("Error: Invalid grade {} (must be between 0 and 100)", v);
                process::exit(1);
            }
            Err(_) => {
                eprintln!("Error: Invalid grade (must be between 0 and 100)");
                process::exit(1);
            }
        };
        grades.push(g);
    }
    let course = Course {
        course_name: course_name_in.clone(),
        course_code,
    };
    let mut student = Student {
        name: String::new(),
        age: 0,
        enrolled_course: course,
        grades,
    };
    updateStudentInfo(&mut student, Some(student_name.as_str()), age);
    println!("\nStudent Information:");
    println!("------------------");
    println!("Name: {}", student.name);
    println!("Age: {}", student.age);
    println!(
        "Course: {} (Code: {})", student.enrolled_course.course_name, student
        .enrolled_course.course_code
    );
    print!("Grades: ");
    for g in &student.grades {
        print!("{:.1} ", g);
    }
    println!();
    if !student.grades.is_empty() {
        let sum: f32 = student.grades.iter().copied().sum();
        println!("Average Grade: {:.2}", sum / (student.grades.len() as f32));
    }
}

